mapper.select:
select

from urlControl
where 1=1 

	and id = #{id,jdbcType=INTEGER} 


	and menu = #{menu,jdbcType=VARCHAR} 


	and name = #{name,jdbcType=VARCHAR} 


	and url = #{url,jdbcType=VARCHAR} 


	and status = #{status,jdbcType=INTEGER} 


	and orderBy = #{orderBy,jdbcType=INTEGER}

mapper.insert:
insert into urlControl (id, menu, name, url, status, orderBy) select A.* from (
select #{obj.id,jdbcType=INTEGER}, #{obj.menu,jdbcType=VARCHAR}, #{obj.name,jdbcType=VARCHAR}, #{obj.url,jdbcType=VARCHAR}, #{obj.status,jdbcType=INTEGER}, #{obj.orderBy,jdbcType=INTEGER} from dual
) A

mapper.delete:
delete from urlControl
where 1=1 

	and id = #{id,jdbcType=INTEGER} 


	and menu = #{menu,jdbcType=VARCHAR} 


	and name = #{name,jdbcType=VARCHAR} 


	and url = #{url,jdbcType=VARCHAR} 


	and status = #{status,jdbcType=INTEGER} 


	and orderBy = #{orderBy,jdbcType=INTEGER}
